mapper.insert:
INSERT INTO contact_tbl (
    name
    , phone_number
    , zip_number
    , email
) VALUES (
    #{name}
    , #{phoneNumber}
    , #{zipNumber}
    , #{email}
)

mapper.update:
UPDATE contact_tbl SET
    name = #{name}
    , phone_number = #{phoneNumber}
    , zip_number = #{zipNumber}
    , email = #{email}
WHERE id = #{id}
